    "SCHEMA_REGISTRY_SSL_KEYSTORE_PASSWORD": "changeit",
    "SCHEMA_REGISTRY_SSL_TRUSTSTORE_LOCATION": "/tmp/kafka-truststore.jks",
    "SCHEMA_REGISTRY_SSL_CLIENT_AUTH": "false",
    "SCHEMA_REGISTRY_KAFKASTORE_SASL_MECHANISM": "GSSAPI",
